defmodule Solution do

  defmodule TrieNode do
    defstruct leaf: false, next: %{}
  end

  defmodule Trie do
    def new(), do: %TrieNode{}

    def add(trie, ""), do: %TrieNode{trie | leaf: true}
    def add(trie, <<char, tail::binary>>) do
      Map.update(trie.next, char, add(%TrieNode{}, tail), &add(&1, tail))
      |> (& %TrieNode{trie | next: &1}).()
    end
  end

  def create_trie(words) do
    for word <- words, reduce: Trie.new() do
      acc -> Trie.add(acc, word)
    end
  end

  def create_dict(board) do
    for {row, r} <- Enum.with_index(board),
        {char, c} <- Enum.with_index(row),
        into: %{}, do: {{r, c}, :binary.first(char)}
  end

  def traverse(row, col, node, word, {result, visited} = acc, {row_amnt, col_amnt, board} = const) do
    if row < 0 or col < 0 or row == row_amnt or col == col_amnt
    or board[{row, col}] not in Map.keys(node.next)
    or MapSet.member?(visited, {row, col}) do
      acc
    else
      candidate_char = board[{row, col}]
      new_visited = MapSet.put(visited, {row, col})
      new_node =  node.next[board[{row, col}]]
      new_word = word ++ [candidate_char]
      new_result = if new_node.leaf, do: MapSet.put(result, to_string(new_word)), else: result

      for {new_r, new_c} <- [{row-1, col}, {row+1, col}, {row, col-1}, {row, col+1}],
        reduce: {new_result, new_visited} do
        acc -> traverse(new_r, new_c, new_node, new_word, acc, const)
      end
      |> (fn {r, v} -> {r, MapSet.delete(v, {row, col})} end).()
    end
  end

  @spec find_words(board :: [[char]], words :: [String.t]) :: [String.t]
  def find_words(board, words) do
    uniq_chars = List.flatten(board) |> Enum.uniq |> MapSet.new
    filtered_words = Enum.filter(words, fn word ->
      String.graphemes(word) |> Enum.uniq |> MapSet.new
      |> MapSet.subset?(uniq_chars)
    end)

    trie = create_trie(filtered_words)
    dict = create_dict(board)
    row_amnt = length(board)
    col_amnt = Enum.at(board, 0) |> length
    for r <- 0..row_amnt-1,
        c <- 0..col_amnt-1,
        reduce: {MapSet.new(), MapSet.new()} do # result, visited
        acc -> traverse(r, c, trie, [], acc, {row_amnt, col_amnt, dict})
    end
    |> elem(0) |> MapSet.to_list
  end
end

